import "core-js/modules/es.array.push.js";
export default {
  name: "classi-fication",
  data() {
    return {
      //关于表格区域的
      tableData: [],
      tableLoading: false,
      pageSize: 10,
      total: 0,
      currentPage: 1,
      configType: 1,
      selection: [],
      configId: null,
      updateTime: null,
      //Dialog表单相关的
      dialogVisible: false,
      carouselId: null,
      ruleFrom: {
        configName: "",
        redirectUrl: "",
        goodsId: null,
        configRank: ""
      },
      rules: {
        configName: [{
          required: true,
          message: '年龄不能为空',
          trigger: 'blur'
        }],
        redirectUrl: [{
          required: true,
          message: '年龄不能为空',
          trigger: 'blur'
        }],
        goodsId: [{
          required: true,
          message: '年龄不能为空',
          trigger: 'blur'
        }],
        configRank: [{
          required: true,
          message: '年龄不能为空',
          trigger: 'blur'
        }]
      }
    };
  },
  created() {
    this.init();
  },
  methods: {
    /* 关于table表格的 */
    //获取数据
    async init() {
      this.tableLoading = true;
      try {
        let {
          resultCode,
          data
        } = await this.$api.GoodsSetting.queryList(this.currentPage, this.pageSize);
        if (+resultCode === 200) {
          let {
            totalCount,
            list
          } = data;
          this.total = +totalCount;
          this.tableData = list;
          this.tableLoading = false;
          return;
        }
      } catch (_) {
        console.log('错误：', _);
      }
      this.tableData = [];
      this.total = 0;
      this.tableLoading = false;
    },
    //点击分页
    changePage(num) {
      this.currentPage = num;
      this.init();
    },
    //时间格式化
    formatterTime(row) {
      return _.formatTime(row.createTime, '{1}-{2} {3}:{4}');
    },
    //删除
    async handleRemove(id) {
      let ids = [];
      if (id) {
        //单一删除
        ids.push(id);
      } else {
        //批量删除
        ids = this.selection.map(item => item.goodsId);
      }
      if (ids.length === 0) {
        this.$message.warning('请至少选择一项删除！');
        return;
      }
      try {
        let {
          resultCode
        } = await this.$api.GoodsSetting.removeGood(ids);
        if (+resultCode === 200) {
          this.$message.success('恭喜您，删除成功！');
          this.init();
        } else {
          this.$message.error('删除失败，请稍后再试！');
        }
      } catch (_) {
        console.log('错误：', _);
      }
    },
    //点击修改
    async handleUpdate(row) {
      this.dialogVisible = true;
      try {
        let {
          resultCode,
          data
        } = await this.$api.GoodsSetting.info(row.configId);
        if (+resultCode === 200) {
          this.ruleFrom = data;
          this.configId = data.configId;
          this.updateTime = data.updateTime;
          return;
        }
        this.$message.error('获取失败！');
      } catch (_) {
        console.log('错误：', _);
      }
    },
    selectionChange(val) {
      this.selection = val;
    },
    /* 关于Dialog的 */
    //关闭弹窗
    handleClose() {
      this.$refs.fromIns.resetFields();
      this.ruleFrom = {
        configName: "",
        redirectUrl: "",
        goodsId: 0,
        configRank: 0
      };
      this.dialogVisible = false;
    },
    //添加
    async Submit() {
      try {
        await this.$refs.fromIns.validate();
        //添加
        let fun = this.$api.GoodsSetting.addGood,
          data = {
            ...this.ruleFrom
          };

        //修改
        if (data.goodsId) {
          fun = this.$api.GoodsSetting.updataGood;
          data.configId = this.configId;
          data.createTime = this.updateTime;
        }
        data.configType = 3;
        console.log(data);
        let {
          resultCode
        } = await fun(data);
        if (+resultCode === 200) {
          this.handleClose();
          this.$message.success('恭喜您，操作成功！');
          this.init();
          return;
        } else {
          this.$message.error('操作失败，请稍后再试！');
        }
      } catch (_) {
        console.log('错误：', _);
      }
    },
    handleChange(value) {
      console.log(value);
    },
    move() {
      // this.flag = true;
    }
  }
};
