/* 热销商品设置板块的接口 */
import http from "./http";

const queryList = (pageNumber = 1, pageSize = 0, configType = 3) => {
    return http.get('/indexConfigs', {
        params: {
            pageNumber,
            pageSize,
            configType
        }
    })
}

const addGood = (data) => {
    return http.post('/indexConfigs', data)
}

//删除热销商品
