import http from "./http"

//获取当前登录者信息
const queryData = () => {
    return http.get('/adminUser/profile')
}

//修改登录名和昵称
const updateName = (loginUserNmae, nickName) => {
    return http.put('/adminUser/name', {
        loginUserNmae,
        nickName
    })
}

export default {
    queryData,
    updateName
}
