import http from "./http";

/* 
分类管理模块相关接口
*/
//获取信息列表信息
const queryList = (pageNumber = 1, pageSize = 10, categoryLevel = 1, parentId = 0) => {
    return http.get('/categories', {
        params: {
            pageNumber,
            pageSize,
            categoryLevel,
            parentId
        }
    })
}

//删除信息
const removeClassify = (ids) => {
    return http.delete('/categories', ids)
}

//修改分类信息


export default {
    queryList,
    removeClassify
}
