/* 热销商品设置板块的接口 */
import http from "./http";

const queryList = (pageNumber = 1, pageSize = 0, configType = 3) => {
    return http.get('/indexConfigs', {
        params: {
            pageNumber,
            pageSize,
            configType
        }
    })
}

const addGood = (configName,
    configType,
    redirectUrl,
    configRank) => {
    return http.post('/indexConfigs', {
        configName,
        configType,
        redirectUrl,
        configRank
    })
}


export default {
    queryList,

}
