// 新品上线设置的接口
import http from "./http";

//获取新品上线列表信息
const queryProductList = (pageNumber = 1, pageSize = 10, configType = 4) => {
    return http.get('/indexConfigs', {
        params: {
            pageNumber,
            pageSize,
            configType
        }
    })
}
// 获取详细信息---新品上线
const info = (id) => {
    return http.get(`/indexConfigs/${id}`)
}

//删除---新品上线
const removeProduct = (ids) => {
    return http.post('/indexConfigs/delete', {
        data: {
            ids
        }
    })
}



/* 为你推荐 */

//获取为你推荐列表信息
const queryRecommendList = (pageNumber = 1, pageSize = 10, configType = 5) => {
    return http.get('/indexConfigs', {
        params: {
            pageNumber,
            pageSize,
            configType
        }
    })
}



export default {
    queryProductList,
    queryRecommendList,
    removeProduct,
    info
}
